/**
/**
 * Format action based on rule type and rule configuration
 * @param {Object} rule The rule configuration
 * @param {string} ruleType The type of rule being processed
 * @returns {string} The formatted action string
 */
function formatAction(rule, ruleType) {
    switch (ruleType) {
        case 'columns':
            return `set_column: ${rule.value}`;
        case 'sprints':
            return `set_sprint: ${rule.value}`;
        case 'board_items':
            return 'add_to_board';
        case 'assignees':
            return `add_assignee: ${rule.value}`;
        case 'linked_issues':
            if (!rule.action) {
                throw new Error(`No action specified for linked_issues rule: ${JSON.stringify(rule)}`);
            }
            return rule.action;
        default:
            if (!rule.action) {
                throw new Error(`No action specified for rule type '${ruleType}': ${JSON.stringify(rule)}`);
            }
            return rule.action;
    }
}
